def vision_mamba(input_shape, patch_size, dim, depth, drop_path_rate=0., **kwargs):
    c, h, w = input_shape
    assert h == w
    assert c == 3
    model = VisionMamba(
        img_size=h,
        patch_size=patch_size,
        stride=patch_size,
        embed_dim=dim,
        depth=depth,
        drop_path_rate=drop_path_rate,
        rms_norm=True,
        residual_in_fp32=True,
        fused_add_norm=True,
        final_pool_type='mean',
        if_abs_pos_embed=True,
        if_rope=False,
        if_rope_residual=False,
        bimamba_type="v2",
        if_cls_token=True,
        if_devide_out=True,
        use_middle_cls_token=True,
        **kwargs,
    )
    model.default_cfg = _cfg()
    return model
